export class AbortError extends Error {
  constructor(...args) {
    super(...args);
    this.type = 'AbortError';
  }
}

export class ParseError extends Error {
  constructor(...args) {
    super(...args);
    this.type = 'ParseError';
  }
}

export function assert(statement, err = new Error()) {
  if (statement === false) throw err;
}
